import { precacheAndRoute } from 'workbox-precaching';
import { registerRoute } from 'workbox-routing';
import { StaleWhileRevalidate } from 'workbox-strategies';

// Precache all of the assets generated by workbox-webpack-plugin
precacheAndRoute(self.__WB_MANIFEST);

// Cache API responses with a stale-while-revalidate strategy
registerRoute(
  ({ request }) => request.destination === 'document',
  new StaleWhileRevalidate({
    cacheName: 'pages-cache',
    plugins: [
      {
        cacheDidUpdate: async ({ cacheName, request }) => {
          console.log(`Updated cache ${cacheName} for request:`, request.url);
        },
      },
    ],
  })
);

// Cache images with a stale-while-revalidate strategy
registerRoute(
  ({ request }) => request.destination === 'image',
  new StaleWhileRevalidate({
    cacheName: 'images-cache',
    plugins: [
      {
        cacheDidUpdate: async ({ cacheName, request }) => {
          console.log(`Updated cache ${cacheName} for request:`, request.url);
        },
      },
    ],
  })
);
